from django.contrib.auth.decorators import login_requiredfrom django.contrib.auth.models import Groupfrom django.shortcuts import renderfrom Jira.auth import has_groupfrom app_accounts.models import Employeefrom app_organization.models import Organization, OrgMembers# @has_group(groups=[1, 2, 3, 4, 5])# @login_requireddef HomeView(request):    user = request.user.id    org = Organization.objects.filter(created_by=user)    emp = Employee.objects.all()    # organization = OrgMembers.objects.filter(add=True)    context = {        'org': org,        'employees': emp,    }    return render(request, 'home.html', context=context)