from django import formsfrom django.contrib.auth.models import Userfrom django_countries.fields import CountryFieldfrom django_countries.widgets import CountrySelectWidgetfrom app_accounts.models import Employeeclass LoginForm(forms.Form):    username = forms.CharField(label="Username", widget=forms.TextInput(attrs={'class': 'form-control'}))    password = forms.CharField(label="Password", widget=forms.PasswordInput(attrs={'class': 'form-control'}))class RegisterForm(forms.Form):    first_name = forms.CharField(widget=forms.TextInput(        attrs={'class': 'form-control', 'placeholder': 'First name'}))    last_name = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Last name'}))    email = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Email'}))    username = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'User name'}))    password = forms.CharField(widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': 'Password'}))    confirm_password = forms.CharField(        widget=forms.PasswordInput(attrs={'class': 'form-control', 'placeholder': 'Confirm password'}))    phone = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Phone'}))    profile_image = forms.FileField(widget=forms.FileInput(attrs={'class': 'form-control', 'name': 'profile_image'}))    def is_valid(self):        from app_accounts.service import username_exists, email_exists        data = self.data        username = data.get('username')        email = data.get('email')        password = data.get('password')        confirm_password = data.get('confirm_password')        if username_exists(username):            self.add_error('username', 'This username already taken')            return False        if email_exists(email):            self.add_error('email', 'This email already registered')            return False        if not password.__eq__(confirm_password):            self.add_error('password', 'Password does not match')            self.add_error('confirm_password', 'Password does not match')            return False        return Trueclass UserProfileForm(forms.ModelForm):    class Meta:        model = User        fields = ['first_name', 'last_name', 'username']        widgets = {            'first_name': forms.TextInput(                attrs={'class': 'form-control', 'placeholder': 'First name'}),            "last_name":                forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Last name'}),            # "email": forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Email'}),            "username":                forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'User name'})        }class EmployeeProfileForm(forms.ModelForm):    country = CountryField(blank_label='(select country)').formfield(        required=False,        widget=CountrySelectWidget(attrs={            'class': 'custom-select d-block w-100',        })),    class Meta:        model = Employee        fields = ['phone', 'address', 'country', 'profile_image']        widgets = {            "phone": forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Phone'}),            "profile_image": forms.FileInput(attrs={'class': 'form-control', 'name': 'profile_image'}),            "address": forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Address'}),        }    #    # class UserUpdateForm(forms.ModelForm):    #     class Meta:    #         model = User    #         fields = ['username', 'email']