from django.contrib.auth.views import LogoutViewfrom django.urls import pathfrom app_accounts.views import LoginView, RegisterView, user_profile, ProfileUpdateViewapp_name = 'auth'urlpatterns = [    path('login/', LoginView.as_view(), name='login'),    path('logout/', LogoutView.as_view(), name='logout'),    path('register/', RegisterView.as_view(), name='register'),    path('update_profile/', ProfileUpdateView, name='update_profile'),    path('profile/<int:user_id>', user_profile, name='profile'),]