from django import formsfrom django.forms.utils import ErrorListfrom app_organization.models import Organizationclass OrgForm(forms.Form):    title = forms.CharField(        widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Title'}))    description = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Description'}))    image = forms.ImageField(widget=forms.FileInput(attrs={'class': 'form-control', 'name': 'org_image'}))class OrgUpdateForm(forms.ModelForm):    class Meta:        model = Organization        fields = ['title', 'description', 'image']        widgets = {            'title': forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Title'}),            "description": forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Description'}),            'image': forms.FileInput(attrs={'class': 'form-control', 'name': 'org_image'})        }