from django.contrib.auth.models import User, Groupfrom django.db import transactionfrom app_accounts.models import Employeefrom app_organization.forms import OrgFormfrom app_organization.models import Organization, OrgMembersfrom app_uploads.utils import upload@transaction.atomicdef create_org(form: OrgForm, user: User, employee: Employee) -> Organization:    data = form.data    org = Organization()    org.title = data.get('title')    org.created_by = user    org.description = data.get('description')    org.image = form.files.get('image')    org.save()    group = Group.objects.get(pk=1)    orgmem = OrgMembers()    orgmem.org_id = org.pk    orgmem.add = True    orgmem.role = group    orgmem.created_by = user    orgmem.user = employee    orgmem.save()    return org