from django.urls import pathfrom app_organization.views import (    # HomeView,    OrgCreate,    org_delete,    org_delete_page,    OrgDetail,    update_role,    add_member,    delete_member,    members,    OrgUpdateView,    employee_roles,    add_members,    connect_organization,    searched)app_name = 'organization'urlpatterns = [    path('org_create', OrgCreate.as_view(), name='org_create'),    path('org_detail/<int:org_id>/', OrgDetail, name='org_detail'),    path('org_delete_page/<int:org_id>/', org_delete_page, name='org_delete_page'),    path('org_delete/<int:org_id>/', org_delete, name='org_delete'),    path('org_update/<int:org_pk>/', OrgUpdateView, name='org_update'),    path('<int:org_pk>/add_member/<user_pk>', add_member, name='add_employee'),    path('<int:org_pk>/update_role/<user_pk>/<role_pk>', update_role, name='update_role'),    path('<int:org_pk>/update_role_list/<int:user_id>', employee_roles, name='employee_roles_page'),    path('<int:org_pk>/delete_member/<user_pk>', delete_member, name='delete_employee'),    path('<int:org_pk>/member_list/', members, name='employee_list'),    path('<int:org_pk>/add_members_list/', add_members, name='add_employee_list'),    path('searched/', searched, name='searched'),    path('organization_connect/', connect_organization, name='connect_organization'),]