from django import formsclass PrjForm(forms.Form):    title = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Project Title'}))    description = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Project Description'}))    image = forms.FileField(widget=forms.FileInput(attrs={'class': 'form-control', 'name': 'Project image'}))