from django import formsfrom app_task.models import Commentclass TaskForm(forms.Form):    title = forms.CharField(widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Project Title'}))    description = forms.CharField(        widget=forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Project Description'}))    image = forms.FileField(widget=forms.FileInput(attrs={'class': 'form-control', 'name': 'Project image'}))class CommentForm(forms.Form):    text = forms.CharField(widget=forms.Textarea(attrs={'class': 'form-control', 'placeholder': 'Comment'}))