from django.urls import pathfrom app_task.views import (CreateTask,                            add_comment_to_post,                            comment_approve,                            comment_remove,                            TaskDetail, task_add_member, task_delete_member, task_members)app_name = 'task'urlpatterns = [    path('create_task/<int:pk>', CreateTask, name="create_task"),    path('task_detail/<int:task_id>', TaskDetail, name='task_detail'),    path('post/<int:pk>/comment/', add_comment_to_post, name='add_comment_to_post'),    path('comment/<int:pk>/approve/', comment_approve, name='comment_approve'),    path('comment/<int:pk>/remove/', comment_remove, name='comment_remove'),    path('<int:task_id>/task_add_member/<int:user_pk>', task_add_member, name='task_add_member'),    path('<int:task_id>/task_delete_member/<int:user_pk>', task_delete_member, name='task_delete_member'),    path('<int:task_id>/task_member_list/', task_members, name='task_member_list'),]